import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Container, Typography, Box, TextField, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton, Snackbar, Alert, CircularProgress
} from "@mui/material";
import { DateTimePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import dayjs from "dayjs";
import utc from "dayjs/plugin/utc";
import timezone from "dayjs/plugin/timezone";

// Configure dayjs with plugins
dayjs.extend(utc);
dayjs.extend(timezone);
dayjs.tz.setDefault("Asia/Kolkata"); // Set IST as default timezone

const API_URL = "http://localhost:8000";

function App() {
  const [jobs, setJobs] = useState([]);
  const [name, setName] = useState("");
  const [scheduledTime, setScheduledTime] = useState(null);
  const [loading, setLoading] = useState(false);
  const [editingJob, setEditingJob] = useState(null);
  const [editName, setEditName] = useState("");
  const [editScheduledTime, setEditScheduledTime] = useState(null);
  const [error, setError] = useState("");

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API_URL}/jobs/`);
      setJobs(res.data);
    } catch (err) {
      setError("Failed to fetch jobs");
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !scheduledTime) return;
    try {
      // Convert to UTC ISO string while preserving the selected IST time
      const utcTime = dayjs(scheduledTime).tz("Asia/Kolkata").utc().format();

      await axios.post(`${API_URL}/jobs/`, {
        name,
        scheduled_time: utcTime,
      });
      setName("");
      setScheduledTime(null);
      fetchJobs();
    } catch (err) {
      setError("Failed to create job");
    }
  };

  const handleEdit = (job) => {
    setEditingJob(job.id);
    setEditName(job.name);
    setEditScheduledTime(dayjs(job.scheduled_time).tz("Asia/Kolkata"));
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      // Convert to UTC ISO string while preserving the selected IST time
      const utcTime = dayjs(editScheduledTime).tz("Asia/Kolkata").utc().format();

      await axios.put(`${API_URL}/jobs/${editingJob}`, {
        name: editName,
        scheduled_time: utcTime,
      });
      setEditingJob(null);
      fetchJobs();
    } catch (err) {
      setError("Failed to update job");
    }
  };

  const handleDelete = async (jobId) => {
    if (!window.confirm("Delete this job?")) return;
    try {
      await axios.delete(`${API_URL}/jobs/${jobId}`);
      fetchJobs();
    } catch (err) {
      setError("Failed to delete job");
    }
  };

  const handleManualRun = async (jobId) => {
    try {
      await axios.post(`${API_URL}/jobs/${jobId}/run`);
      fetchJobs();
    } catch (err) {
      setError("Failed to run job");
    }
  };

  // Format time to IST with timezone indicator
  const formatToIST = (time) => {
    return dayjs(time).tz("Asia/Kolkata").format('YYYY-MM-DD HH:mm [IST]');
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Scheduler App
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 2, mb: 3, alignItems: 'center' }}>
          <TextField
            label="Job Name"
            variant="outlined"
            value={name}
            onChange={e => setName(e.target.value)}
            required
            sx={{ flex: 2 }}
          />
          <DateTimePicker
            label="Scheduled Time (IST)"
            value={scheduledTime}
            onChange={setScheduledTime}
            timezone="Asia/Kolkata"
            sx={{ flex: 2 }}
          />
          <Button type="submit" variant="contained" color="primary" sx={{ flex: 1 }}>
            Create Job
          </Button>
        </Box>
        <Typography variant="h6" gutterBottom>Jobs</Typography>
        {loading ? (
          <Box display="flex" justifyContent="center" alignItems="center" minHeight={100}>
            <CircularProgress />
          </Box>
        ) : (
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Scheduled (IST)</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {jobs.map(job => (
                  <TableRow key={job.id}>
                    {editingJob === job.id ? (
                      <>
                        <TableCell>
                          <TextField
                            value={editName}
                            onChange={e => setEditName(e.target.value)}
                            size="small"
                          />
                        </TableCell>
                        <TableCell>{job.status}</TableCell>
                        <TableCell>
                          <DateTimePicker
                            value={editScheduledTime}
                            onChange={setEditScheduledTime}
                            timezone="Asia/Kolkata"
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          <Button onClick={handleEditSubmit} variant="contained" size="small" sx={{ mr: 1 }}>Save</Button>
                          <Button onClick={() => setEditingJob(null)} size="small">Cancel</Button>
                        </TableCell>
                      </>
                    ) : (
                      <>
                        <TableCell>{job.name}</TableCell>
                        <TableCell>{job.status}</TableCell>
                        <TableCell>{formatToIST(job.scheduled_time)}</TableCell>
                        <TableCell>
                          <IconButton onClick={() => handleEdit(job)} color="primary"><EditIcon /></IconButton>
                          <IconButton onClick={() => handleDelete(job.id)} color="error"><DeleteIcon /></IconButton>
                          <IconButton onClick={() => handleManualRun(job.id)} color="success"><PlayArrowIcon /></IconButton>
                        </TableCell>
                      </>
                    )}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
        <Snackbar open={!!error} autoHideDuration={4000} onClose={() => setError("")}> 
          <Alert severity="error" onClose={() => setError("")}>{error}</Alert>
        </Snackbar>
      </Container>
    </LocalizationProvider>
  );
}

export default App;
