import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:8000";

function App() {
  const [jobs, setJobs] = useState([]);
  const [name, setName] = useState("");
  const [scheduledTime, setScheduledTime] = useState("");
  const [loading, setLoading] = useState(false);
  const [editingJob, setEditingJob] = useState(null);
  const [editName, setEditName] = useState("");
  const [editScheduledTime, setEditScheduledTime] = useState("");

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API_URL}/jobs/`);
      setJobs(res.data);
    } catch (err) {
      // handle error
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !scheduledTime) return;
    try {
      await axios.post(`${API_URL}/jobs/`, {
        name,
        scheduled_time: scheduledTime,
      });
      setName("");
      setScheduledTime("");
      fetchJobs();
    } catch (err) {
      // handle error
    }
  };

  const handleEdit = (job) => {
    setEditingJob(job.id);
    setEditName(job.name);
    setEditScheduledTime(job.scheduled_time.slice(0, 16)); // for datetime-local
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/jobs/${editingJob}`, {
        name: editName,
        scheduled_time: editScheduledTime,
      });
      setEditingJob(null);
      fetchJobs();
    } catch (err) {
      // handle error
    }
  };

  const handleDelete = async (jobId) => {
    if (!window.confirm("Delete this job?")) return;
    try {
      await axios.delete(`${API_URL}/jobs/${jobId}`);
      fetchJobs();
    } catch (err) {
      // handle error
    }
  };

  const handleManualRun = async (jobId) => {
    try {
      await axios.post(`${API_URL}/jobs/${jobId}/run`);
      fetchJobs();
    } catch (err) {
      // handle error
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "sans-serif" }}>
      <h2>Scheduler App</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
        <input
          type="text"
          placeholder="Job name"
          value={name}
          onChange={e => setName(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <input
          type="datetime-local"
          value={scheduledTime}
          onChange={e => setScheduledTime(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <button type="submit">Create Job</button>
      </form>
      <h3>Jobs</h3>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th style={{ textAlign: "left" }}>Name</th>
              <th>Status</th>
              <th>Scheduled</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {jobs.map(job => (
              <tr key={job.id}>
                {editingJob === job.id ? (
                  <>
                    <td>
                      <input
                        type="text"
                        value={editName}
                        onChange={e => setEditName(e.target.value)}
                      />
                    </td>
                    <td>{job.status}</td>
                    <td>
                      <input
                        type="datetime-local"
                        value={editScheduledTime}
                        onChange={e => setEditScheduledTime(e.target.value)}
                      />
                    </td>
                    <td>
                      <button onClick={handleEditSubmit} style={{ marginRight: 8 }}>Save</button>
                      <button onClick={() => setEditingJob(null)}>Cancel</button>
                    </td>
                  </>
                ) : (
                  <>
                    <td>{job.name}</td>
                    <td>{job.status}</td>
                    <td>{new Date(job.scheduled_time).toLocaleString()}</td>
                    <td>
                      <button onClick={() => handleEdit(job)} style={{ marginRight: 8 }}>Edit</button>
                      <button onClick={() => handleDelete(job.id)} style={{ marginRight: 8 }}>Delete</button>
                      <button onClick={() => handleManualRun(job.id)}>Run Now</button>
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
